CREATE SCHEMA sec;  
GO  

CREATE FUNCTION sec.tvf_securitypredicate(@EmployeeKey AS INT, @ParentEmployeeKey AS INT)  
    RETURNS TABLE  
WITH SCHEMABINDING  
AS  
    RETURN SELECT 1 AS tvf_securitypredicate_result
WHERE @EmployeeKey = CAST(REPLACE(USER_NAME(), 'Employee', '') AS INT) OR @ParentEmployeeKey = CAST(REPLACE(USER_NAME(), 'Employee', '') AS INT)  OR USER_NAME() = 'Employee112'
GO

CREATE SECURITY POLICY EmployeeFilter  
ADD FILTER PREDICATE sec.tvf_securitypredicate(EmployeeKey, ParentEmployeeKey)
ON dbo.DimEmployee
WITH (STATE = ON);  
GO

GRANT SELECT ON sec.tvf_securitypredicate TO Employee112;
GRANT SELECT ON sec.tvf_securitypredicate TO Employee189;  
GRANT SELECT ON sec.tvf_securitypredicate TO Employee10; 
GO

EXECUTE AS USER = 'Employee189';  
SELECT * FROM dbo.DimEmployee;
REVERT;

